#!/usr/bin/env node

var argv    = require('yargs').argv;
var actions = require(__dirname+'/../lib/actions.js');

// Generate new hummingbird app
if (argv.n) {
  actions.generate(argv.n);
}



// Start hummingbird app
if (argv._[0]==='start') {
  var hb = actions.hb();
  console.log("Start not available yet!");
}



// Update hummingbird app
if (argv._[0]==='update') {
  var hb = actions.hb();
  console.log("Update not available yet!");
}